package jetbrains.mps.samples.Physics.java.runtime.objects.rendering;

/*Generated by MPS */

import jetbrains.mps.samples.Physics.java.runtime.objects.World;
import processing.core.PApplet;
import jetbrains.mps.samples.Physics.java.runtime.objects.PhysicalEntity;

public class MetricsRendering {
  public static void render(World world, PApplet ctx) {
    StringBuilder str = new StringBuilder();

    // Time of simulation 
    str.append("Elapsed time: ");
    str.append(world.getTime());
    str.append("s");
    if (world.isPaused()) {
      str.append(" [paused]");
    }
    str.append("\n\n");

    for (PhysicalEntity entity : world.getEntities()) {
      str.append(entity.getName());
      str.append(entity.getPosition().toString());
      str.append("\n");
    }

    ctx.fill(255);
    ctx.text(str.toString(), 5, 15);
  }
}
